import React, { useState } from "react";
import "./App.css";

function App() {
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);
  const [scraping, setScraping] = useState(false);

  // üöÄ Call FastAPI /scrape
  const handleScrape = async () => {
    setScraping(true);
    try {
      const response = await fetch("http://127.0.0.1:8000/scrape", {
        method: "POST",
      });
      const data = await response.json();
      setSummary(`üõ´ ${data.status || data.error}`);
    } catch (err) {
      setSummary("‚ö†Ô∏è Error starting scraper.");
    } finally {
      setScraping(false);
    }
  };

  // üß† Call FastAPI /summarize
  const handleSummarize = async () => {
    setLoading(true);
    try {
      const response = await fetch("http://127.0.0.1:8000/summarize");
      const text = await response.text();
      setSummary(text);
    } catch (err) {
      setSummary("‚ö†Ô∏è Error fetching summary.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>‚úàÔ∏è AutoFlights Smart Summarizer</h1>
      <p>Click below to scrape and analyze your latest flight data.</p>

      <button onClick={handleScrape} disabled={scraping}>
        {scraping ? "Scraping..." : "Scrape Latest Flights"}
      </button>

      <button onClick={handleSummarize} disabled={loading}>
        {loading ? "Analyzing..." : "Fetch Flight Summary"}
      </button>

      <pre className="output-box">{summary}</pre>
    </div>
  );
}

export default App;
