import React, { useState } from "react";
import "./App.css";


function App() {
  const [loading, setLoading] = useState(false);
  const [summary, setSummary] = useState("");
  const [flights, setFlights] = useState([]);
  const [table, setTable] = useState("");

  const [origin, setOrigin] = useState("");
  const [destination, setDestination] = useState("");
  const [month, setMonth] = useState("");

  const fetchSummary = async () => {
    setLoading(true);
    setSummary("");
    try {
      const res = await fetch("http://127.0.0.1:8000/summarize");
      const data = await res.json();
      setSummary(data.summary || "No data found.");
    } catch (err) {
      console.error(err);
      setSummary("Error fetching data.");
    } finally {
      setLoading(false);
    }
  };

  //Function to trigger scraper + summarizer
  const handleSearchFlights = async () => {
    if (!origin || !destination || !month) {
      alert("Please fill in all fields before searching!");
      return;
    }

    setLoading(true);
    setSummary("");
    setFlights([]);
    setTable("");

    try {
      const res = await fetch("http://127.0.0.1:8000/scrape", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          origin,
          destination,
          month,
        }),
      });

      const data = await res.json();
      if (data.status === "ok") {
        setFlights(data.flights);
        setTable(data.table);
        setSummary(data.summary);
      } else {
        setSummary(data.message || "No flights found.");
      }
    } catch (err) {
      console.error(err);
      setSummary("Error scraping flights.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "40px", fontFamily: "Arial" }}>
      <h1>‚úàÔ∏è AutoFlights Smart Summarizer</h1>
      <p>Enter trip details to search for new flights, or fetch the last summary.</p>

      {/* ‚úàÔ∏è New Input Form */}
      <div
        style={{
          display: "flex",
          gap: "10px",
          marginBottom: "20px",
          flexWrap: "wrap",
          justifyContent: "center",
        }}
      >
        <input
          type="text"
          placeholder="From (e.g., Dallas)"
          value={origin}
          onChange={(e) => setOrigin(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            width: "200px",
          }}
        />
        <input
          type="text"
          placeholder="To (e.g., Paris)"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            width: "200px",
          }}
        />
        <input
          type="text"
          placeholder="Month (e.g., Jan 2026)"
          value={month}
          onChange={(e) => setMonth(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            width: "200px",
          }}
        />
        <button
          onClick={handleSearchFlights}
          disabled={loading}
          style={{
            backgroundColor: "#28a745",
            color: "white",
            padding: "10px 20px",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          {loading ? "Searching..." : "Search Flights"}
        </button>
      </div>

      {/* Existing fetch summary button */}
      <button
        onClick={fetchSummary}
        disabled={loading}
        style={{
          backgroundColor: "#007bff",
          color: "white",
          padding: "10px 20px",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
        }}
      >
        {loading ? "Analyzing..." : "Fetch Last Summary"}
      </button>

      {/* Results Box */}
      <div
        style={{
          marginTop: "30px",
          padding: "20px",
          border: "1px solid #ccc",
          borderRadius: "10px",
          backgroundColor: "#f9f9f9",
        }}
      >
        {flights.length > 0 && (
  <>
    <h3>üõ´ Scraped Flights</h3>
    <table
      style={{
        width: "100%",
        borderCollapse: "collapse",
        marginTop: "10px",
      }}
    >
      <thead>
        <tr style={{ background: "#eee" }}>
          <th style={{ border: "1px solid #ccc", padding: "6px" }}>Airline</th>
          <th style={{ border: "1px solid #ccc", padding: "6px" }}>Price</th>
          <th style={{ border: "1px solid #ccc", padding: "6px" }}>Duration</th>
          <th style={{ border: "1px solid #ccc", padding: "6px" }}>Stops</th>
          <th style={{ border: "1px solid #ccc", padding: "6px" }}>Departure</th>
          <th style={{ border: "1px solid #ccc", padding: "6px" }}>Arrival</th>
        </tr>
      </thead>
      <tbody>
        {flights.map((f, i) => (
          <tr key={i}>
            <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.airline}</td>
            <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.price}</td>
            <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.duration}</td>
            <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.stops}</td>
            <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.departure}</td>
            <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.arrival}</td>
          </tr>
        ))}
      </tbody>
    </table>
  </>
)}
        <pre style={{ whiteSpace: "pre-wrap", wordWrap: "break-word" }}>
          {summary}
        </pre>
      </div>
    </div>
  );
}

export default App;
