import React, { useState } from "react";
import "./App.css";
import ReactMarkdown from "react-markdown";


function App() {
  const [loading, setLoading] = useState(false);
  const [summary, setSummary] = useState("");
  const [flights, setFlights] = useState([]);

  const [origin, setOrigin] = useState("");
  const [destination, setDestination] = useState("");
  const [month, setMonth] = useState("");

  const [tableFlights, setTableFlights] = useState([]);

  const [logs, setLogs] = useState("");


  const tidySummary = (s) =>
    (s || "")
      .replace(/\*\*Thoughts:\*\*\s*\n+/g, '**Thoughts:**\n')
      .replace(/\n{3,}/g, '\n\n');

  // Function to trigger scraper + summarizer
  const handleSearchFlights = async () => {
    if (!origin || !destination || !month) {
      alert("Please fill in all fields before searching!");
      return;
    }

    setLoading(true);
    setSummary("");
    setFlights([]);
    setLogs("Starting search...\n");

    try {
      const res = await fetch("http://127.0.0.1:8000/scrape", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ origin, destination, month }),
      });

      const reader = res.body.getReader();                
      const decoder = new TextDecoder("utf-8");           
      let accumulated = "";                               

      while (true) {                                      
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value);
        accumulated += chunk;
        setLogs((prev) => prev + chunk);                

        // Detect when backend sends final JSON result
        if (chunk.includes("__RESULT__")) {
          const jsonText = chunk.split("__RESULT__")[1];
          const data = JSON.parse(jsonText);

          if (data.status === "ok") {
            setFlights(data.flights);
            setTableFlights(data.table_flights || []);
            setSummary(data.summary);
          } else {
            setSummary(data.message || "No flights found.");
          }
          break; // ‚úÖ Stop reading once JSON result is received
        }
      }
    } catch (err) {
      console.error(err);
      setLogs((prev) => prev + "\nError scraping flights.\n"); // ‚úÖ NEW - log error to UI
      setSummary("Error scraping flights.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "40px", fontFamily: "Arial" }}>
      <h1>AutoFlights Smart Summarizer</h1>
      <p>Enter trip details to search for new flights.</p>

      {/* ‚úàÔ∏è Input Form */}
      <div
        style={{
          display: "flex",
          gap: "10px",
          marginBottom: "20px",
          flexWrap: "wrap",
          justifyContent: "center",
        }}
      >
        <input
          type="text"
          placeholder="From (e.g., Dallas)"
          value={origin}
          onChange={(e) => setOrigin(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            width: "200px",
          }}
        />
        <input
          type="text"
          placeholder="To (e.g., Paris)"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            width: "200px",
          }}
        />
        <input
          type="text"
          placeholder="Month (e.g., Jan 2026)"
          value={month}
          onChange={(e) => setMonth(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            width: "200px",
          }}
        />
        <button
          onClick={handleSearchFlights}
          disabled={loading}
          style={{
            backgroundColor: "#28a745",
            color: "white",
            padding: "10px 20px",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          {loading ? "Searching..." : "Search Flights"}
        </button>
      </div>

      {/* üõ∞ Live Progress Logs */}
        {logs && (
          <div
            style={{
              marginTop: "20px",
              padding: "15px",
              backgroundColor: "#f0f0f0",
              borderRadius: "8px",
              border: "1px solid #ddd",
              fontFamily: "monospace",
              whiteSpace: "pre-wrap",
              color: "#333",
              maxHeight: "200px",
              overflowY: "auto",
            }}
          >
            {logs}
          </div>
        )}

      {/* Results Box */}
      <div
        style={{
          marginTop: "30px",
          padding: "20px",
          border: "1px solid #ccc",
          borderRadius: "10px",
          backgroundColor: "#f9f9f9",
        }}
      >
        {flights.length > 0 && (
          <>
            <h3 style={{ marginBottom: "10px" }}>Available Flights</h3>
            <table
              style={{
                width: "100%",
                borderCollapse: "collapse",
                marginTop: "10px",
              }}
            >
              <thead>
                <tr style={{ background: "#eee" }}>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Airline</th>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Price</th>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Duration</th>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Stops</th>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Departure</th>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Arrival</th>
                </tr>
              </thead>
              <tbody>
                {flights.map((f, i) => (
                  <tr key={i}>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.airline}</td>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.price}</td>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.duration}</td>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.stops}</td>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.departure}</td>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.arrival}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        )}

        {tableFlights.length > 0 && (
          <>
            <h3 style={{ marginTop: "30px" }}> Top Cheapest Flights</h3>
            <table
              style={{
                width: "100%",
                borderCollapse: "collapse",
                marginTop: "10px",
              }}
            >
              <thead>
                <tr style={{ background: "#eee" }}>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Airline</th>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Price</th>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Duration</th>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Stops</th>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Departure</th>
                  <th style={{ border: "1px solid #ccc", padding: "6px" }}>Arrival</th>
                </tr>
              </thead>
              <tbody>
                {tableFlights.map((f, i) => (
                  <tr key={i}>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.airline}</td>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.price}</td>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.duration}</td>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.stops}</td>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.departure}</td>
                    <td style={{ border: "1px solid #ddd", padding: "6px" }}>{f.arrival}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        )}

        {summary && (
          <div
            style={{
              whiteSpace: "pre-wrap",
              wordWrap: "break-word",
              marginTop: "20px",
              lineHeight: "1.6",
            }}
          >
            <ReactMarkdown
              components={{
                // paragraphs: zero margins so the <p> that wraps **Thoughts:** doesn't add space
                p: ({ node, ...props }) => (
                  <p style={{ marginTop: 0, marginBottom: 0, lineHeight: "1.5" }} {...props} />
                ),
                // make the heading inline and with no margin
                strong: ({ node, ...props }) => (
                  <strong style={{ display: "inline", margin: 0 }} {...props} />
                ),
                // keep list tight but readable
                ul: ({ node, ...props }) => (
                  <ul style={{ marginTop: 2, marginBottom: 4, paddingLeft: 18 }} {...props} />
                ),
                li: ({ node, ...props }) => (
                  <li style={{ marginBottom: 2, lineHeight: "1.5" }} {...props} />
                ),
              }}
            >
  {tidySummary(summary)}
</ReactMarkdown>

          </div>
        )}

      </div>
    </div>
  );
}

export default App;
