import React, { useState } from "react";
import "./App.css";


function App() {
  const [loading, setLoading] = useState(false);
  const [summary, setSummary] = useState("");
  const [flights, setFlights] = useState([]);
  const [table, setTable] = useState("");

  const [origin, setOrigin] = useState("");
  const [destination, setDestination] = useState("");
  const [month, setMonth] = useState("");

  const fetchSummary = async () => {
    setLoading(true);
    setSummary("");
    try {
      const res = await fetch("http://127.0.0.1:8000/summarize");
      const data = await res.json();
      setSummary(data.summary || "No data found.");
    } catch (err) {
      console.error(err);
      setSummary("Error fetching data.");
    } finally {
      setLoading(false);
    }
  };

  //Function to trigger scraper + summarizer
  const handleSearchFlights = async () => {
    if (!origin || !destination || !month) {
      alert("Please fill in all fields before searching!");
      return;
    }

    setLoading(true);
    setSummary("");
    setFlights([]);
    setTable("");

    try {
      const res = await fetch("http://127.0.0.1:8000/scrape", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          origin,
          destination,
          month,
        }),
      });

      const data = await res.json();
      if (data.status === "ok") {
        setFlights(data.flights);
        setTable(data.table);
        setSummary(data.summary);
      } else {
        setSummary(data.message || "No flights found.");
      }
    } catch (err) {
      console.error(err);
      setSummary("Error scraping flights.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "40px", fontFamily: "Arial" }}>
      <h1>✈️ AutoFlights Smart Summarizer</h1>
      <p>Click below to analyze your latest scraped flight data.</p>

      <button
        onClick={fetchSummary}
        disabled={loading}
        style={{
          backgroundColor: "#007bff",
          color: "white",
          padding: "10px 20px",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
        }}
      >
        {loading ? "Analyzing..." : "Fetch Flight Summary"}
      </button>

      <div
        style={{
          marginTop: "30px",
          padding: "20px",
          border: "1px solid #ccc",
          borderRadius: "10px",
          backgroundColor: "#f9f9f9",
        }}
      >
        <pre style={{ whiteSpace: "pre-wrap", wordWrap: "break-word" }}>
          {summary}
        </pre>
      </div>
    </div>
  );
}

export default App;
